import React, {Component} from "react";
import './Info.css';
import { Bar, Line } from 'react-chartjs-2';
import axios from "axios";


class Info extends Component {
    constructor(props) {
        super(props);
        this.state = {
            // parentId: props.location.aboutParent.parentId,
            // firstName: props.location.aboutParent.firstName,
            // lastName: props.location.aboutParent.lastName,
            parentId: null,
            firstName: "",
            lastName: "",
            // studentList : []

            name: "Rakesh",
            users:[],
            students: [],
            parents: [],
            studyTime: [0, 25,0],
            atpq: [0, 2.5, 0],
            scores: [0, 77, 0],
            parent_id: "108",
            student_id: "102",
            skills: 77,
            prog: 10,
            study:6,
            avg: 3

        }

        this.changeWeek1 = this.changeWeek1.bind(this);
        this.changeWeek2 = this.changeWeek2.bind(this);
        this.changeWeek3 = this.changeWeek3.bind(this);
        this.changeWeek4 = this.changeWeek4.bind(this);
        this.changeWeek5 = this.changeWeek5.bind(this);
    }

    componentDidMount() {


        fetch("http://localhost:3000/user").then(res => res.json()).then(res => this.setState({
            users: res
        })).then(res => console.log("res:", res)).catch(err => console.log(err))

        fetch("http://localhost:3000/student").then(res => res.json()).then(res => this.setState({
            students: res
        })).catch(err => console.log(err))

        /*
        fetch("http://localhost:3000/avgtime").then(res => res.json()).then(res => this.setState({
         atpq: res
        })).catch(err => console.log(err))


        fetch("http://localhost:3000/sessions").then(res => res.json()).then(res => this.setState({
         studyTime: res
        })).catch(err => console.log(err))
        fetch("http://localhost:3000/score").then(res => res.json()).then(res => this.setState({
         scores: res
        })).catch(err => console.log(err))
        */

        fetch("http://localhost:3000/parent").then(res => res.json()).then(res => this.setState({
            parents: res
        })).catch(err => console.log(err))


        axios.get('http://localhost:4000/students'
            // ,{params: {
            //     id: this.state.parentId
            // }}
            )
            .then(response => {
                // create an array of contacts only with relevant data
                const students = response.data.map(c => {
                    return {
                        id: c.id,
                        first_name: c.first_name,
                        last_name: c.last_name,
                        email: c.email,
                        school: c.school,
                        grade: c.grade
                    };
                });

                // create a new "State" object without mutating
                // the original State object.
                const newState = Object.assign({}, this.state, {
                    studentList: students
                });

                // store the new state object in the component's state

                this.setState(newState);


                console.log(this.state.studentList[0]);
            })
            .catch(error => console.log(error));

    }



    changeWeek1() {
        console.log(1);
        this.setState({
            studyTime: [0,6,0],
            atpq: [0,2,0],
            scores: [0,35,0],
            skills: 35,
            prog: 10,
            study:6,
            avg: 2
        })
    }

    changeWeek2() {
        console.log(2);
        this.setState({
            studyTime: [6,5,0],
            atpq: [2,3,0],
            scores: [35,55,0],
            skills: 55,
            prog: 10,
            study:5,
            avg: 3
        })

    }

    changeWeek3() {
        console.log(3);
        this.setState({
            studyTime: [5,7,0],
            atpq: [3,1,0],
            scores: [55,62,0],
            skills: 62,
            prog: 10,
            study:7,
            avg: 3
        })

    }

    changeWeek4() {
        console.log(4);
        this.setState({
            studyTime: [7,4,0],
            atpq: [1,3,0],
            scores: [62,70,0],
            skills: 70,
            prog: 10,
            study:4,
            avg: 3
        })

    }

    changeWeek5() {
        console.log(5);
        this.setState({
            studyTime: [4,3,0],
            atpq: [3,5,0],
            scores: [70,77,0],
