            //     id: this.state.parentId
            // }}
            )
            .then(response => {
                // create an array of contacts only with relevant data
                const students = response.data.map(c => {
                    return {
                        id: c.id,
                        first_name: c.first_name,
                        last_name: c.last_name,
                        email: c.email,
                        school: c.school,
                        grade: c.grade
                    };
                });

